precision highp float;
precision highp int;

#define UNIFORM_INSERTION_POINT

varying vec2 vPosition;

void main()
{
    vec2 uv = vPosition;
    gl_FragColor.rgb = vec3(sin(uv*3.14+time)*.5+.5, sin(time)*.5+.5);
    gl_FragColor.a = 1.0;
}

