precision highp int;

#define UNIFORM_INSERTION_POINT

attribute highp vec4 vertex; 

varying vec2 vPosition;
void main(void) {
    vPosition = vertex.xy;
    gl_Position = vec4(vPosition*2.-1., 0., 1.);
}
